INSERT INTO `XXX_glossar_daten` SET glossar_id='15', glossar_lang_id='1', glossar_link='', glossar_descrip='Als Meteorismus oder Blähsucht wird eine übermäßige Ansammlung von Gas im Verdauungstrakt, meist Dickdarm (Meteorismus intestinalis) oder Magen, selten in der freien Bauchhöhle (Meteorismus peritonealis), bezeichnet. Zu einem wesentlichen Abgang von Darmgasen kommt es dabei nicht. Da diese Gasansammlungen allerdings überwiegend bei (funktionellen) Störungen im Dickdarmbereich auftreten, wird auch in der medizinischen Fachliteratur oft gleichbedeutend von Flatulenz oder Blähungen als störender Gasbildung im Magen-Darm-Bereich gesprochen.', glossar_descrip_sans='Als Meteorismus oder Blähsucht wird eine übermäßige Ansammlung von Gas im Verdauungstrakt, meist Dickdarm (Meteorismus intestinalis) oder Magen, selten in der freien Bauchhöhle (Meteorismus peritonealis), bezeichnet. Zu einem wesentlichen Abgang von Darmgasen kommt es dabei nicht. Da diese Gasansammlungen allerdings überwiegend bei (funktionellen) Störungen im Dickdarmbereich auftreten, wird auch in der medizinischen Fachliteratur oft gleichbedeutend von Flatulenz oder Blähungen als störender Gasbildung im Magen-Darm-Bereich gesprochen.\r\n', glossar_Wort='Meteorismus', glossar_Wort_alt='', glossar_gramatinfo='', glossar_abk='', glossar_sachgebiet='', glossar_frequenz='', glossar_definition='', glossar_anwendungsbeispiel='', glossar_siehe='', glossar_synonym1='', glossar_synonym2='', glossar_synonym3='', glossar_synonym4='', glossar_synonym5='', glossar_meta_title='', glossar_meta_descrip='', glossar_meta_key=''  ; ##b_dump##
